import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [posts, setPosts] = useState([]);
  const [form, setForm] = useState({ title: '', body: '' });
  const [editingPostId, setEditingPostId] = useState(null);
const [editForm, setEditForm] = useState({ title: '', body: '' });


  // Fetch posts from backend
  useEffect(() => {
    axios.get('http://localhost:5000/api/posts')
      .then(res => setPosts(res.data))
      .catch(err => console.error('Fetch error:', err));
  }, []);

  // Handle post submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.title || !form.body) return;

    try {
      const res = await axios.post('http://localhost:5000/api/posts', form);
      setPosts([res.data, ...posts]);
      setForm({ title: '', body: '' }); // Clear form
    } catch (err) {
      console.error('Submit error:', err);
    }
  };

  // Delete a post
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/posts/${id}`);
      setPosts(posts.filter(post => post._id !== id));
    } catch (err) {
      console.error('Delete error:', err);
    }
  };

  // Update a post (hardcoded for now)
 const handleUpdate = async (id) => {
  try {
    const res = await axios.put(`http://localhost:5000/api/posts/${id}`, editForm);
    setPosts(posts.map(post => (post._id === id ? res.data : post)));
    setEditingPostId(null); // exit editing mode
    setEditForm({ title: '', body: '' }); // reset form
  } catch (err) {
    console.error('Update error:', err);
  }
};


  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif', maxWidth: '600px', margin: '0 auto' }}>
      <h1>📝 Dev Journal</h1>

      <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
        <input
          type="text"
          placeholder="Post title"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
          style={{ width: '100%', padding: '0.5rem', marginBottom: '0.5rem' }}
        />
        <textarea
          placeholder="Post body"
          value={form.body}
          onChange={(e) => setForm({ ...form, body: e.target.value })}
          style={{ width: '100%', padding: '0.5rem', minHeight: '100px' }}
        />
        <button type="submit" style={{ marginTop: '0.5rem', padding: '0.5rem 1rem' }}>Add Post</button>
      </form>

      {posts.length === 0 ? (
        <p>No posts yet.</p>
      ) : (
        posts.map(post => (
          <div key={post._id} style={{
            border: '1px solid #ccc',
            borderRadius: '8px',
            padding: '1rem',
            marginBottom: '1rem',
            position: 'relative'
          }}>
            
            <h2>{post.title}</h2>
            <p>{post.body}</p>
            <small>{new Date(post.date).toLocaleString()}</small>

            <div style={{ marginTop: '1rem', display: 'flex', gap: '0.5rem' }}>
              <button
                onClick={() => handleUpdate(post._id)}
                style={{
                  backgroundColor: '#457b9d',
                  color: '#fff',
                  border: 'none',
                  padding: '0.4rem 0.8rem',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                Update
              </button>
              <button
                onClick={() => handleDelete(post._id)}
                style={{
                  backgroundColor: '#e63946',
                  color: '#fff',
                  border: 'none',
                  padding: '0.4rem 0.8rem',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                Delete
              </button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default App;
